import React from "react";
import { StyleSheet, Text, View } from 'react-native';
import { Avatar } from "react-native-elements";
import { Input, Button } from 'react-native-elements';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Icon from 'react-native-vector-icons/FontAwesome';

import axios from 'axios';



const CadastroScreen = ({route,navigation}) => {  

  const [getNome,setNome] = useState();
const [getCpf,setCpf] = useState();
const [getTelefone,setTelefone] = useState();
const [getId,setId] = useState();
const [getAlterar,setAlterar] = useState();

useEffect(()=>{
    if(route.params){
        const { nome } =  route.params 
        const { telefone } =  route.params 
        const { cpf } =  route.params 
        const { id } =  route.params
        const { alterar } =  route.params

        setNome(nome)
        setTelefone(telefone)
        setCpf(cpf)
        setId(id)
        setAlterar(alterar)
    }

},[]) 

async function inserirDados(){
        
    axios.post('http://professornilson.com/testeservico/clientes', {
        nome: getNome,
        telefone: getTelefone,
        cpf: getCpf
      })
      .then(function (response) {
        setNome('');
        setCpf('');
        setTelefone(''); 
        showMessage({
            message: "Registro Cadastrado com sucesso",
            type: "success",
          }); 
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });     
    
}




    return (
      <View style={[styles.container, {
        flexDirection: "column"
      }]}>
  
        <View style={{ flex: 0.5, backgroundColor: "#00A8FF", alignItems: "center", }}>
  
          <Text h1 style={{fontSize:30, color:"white"}}>  
          <Icon onPress={()=>navigation.navigate('Login')} 
             name='arrow-left'
             size={30}
             color='white'
           />Usuário
          </Text>

          </View>

          <View style={{ flex: 3, backgroundColor: "white" }}>
       
       {/*  <Text style={{ paddingTop: 30, textAlign: "left" }}>Nome</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
            />
  
            <Text style={{ paddingTop: 30, textAlign: "left" }}>Cpf</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
            />


            <Text style={{ paddingTop: 30, textAlign: "left" }}>Email</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
            />
            <Text>Senha</Text>
            <Input placeholder="" secureTextEntry={true}
              textAlign="center"
            /> */}
          
  
        </View>
  
  
       
      <View style={{top:"80px", flex: 2 }} >

{/* <Button title="Salvar"  style={{ paddingTop: 10 }} /> */}
<Button  
            title="< Voltar"
            onPress={()=>navigation.navigate('Lista de Contatos')}
            ></Button>


</View>

<Text style={styles.titulo}>Digite seu Nome</Text>
            <TextInput
            style={{ height: 40,width:300, borderColor: 'gray', borderWidth: 1 }}
            onChangeText={text => setNome(text)}
            value={getNome}
            />    

            <Text style={styles.titulo}>Digite seu Cpf</Text>
            <TextInput
            style={{ height: 40,width:300, borderColor: 'gray', borderWidth: 1 }}
            onChangeText={text => setCpf(text)}
            value={getCpf}
            /> 

            <Text style={styles.titulo}>Digite seu Telefone</Text>
            <TextInput
            style={{ height: 40,width:300, borderColor: 'gray', borderWidth: 1 }}
            onChangeText={text => setTelefone(text)}
            value={getTelefone}
            />     

            { !getAlterar ? (
            <Button style={{paddingTop:20}}
            title="Salvar"
            style={styles.botao}
            onPress={() => inserirDados()}
            />
            ):null}

            { getAlterar ? (
            <Button style={{paddingTop:20}}
            title="Alterar"
            style={styles.botao}
            onPress={() => alterarDados()}
            />
            ):null}
            
            { getAlterar ? (
            <Button style={{paddingTop:20}}
            title="Excluir"
            color="red"
            linearGradientProps={{
                colors: ['red','red', 'red'],
                }}
            style={styles.botaoExcluir}
            onPress={() => excluirDados()}
            />
            ):null}
      </View> 
    );
  };
  

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 20,
    },
  });


  export default CadastroScreen;
