import React from "react";
import { StyleSheet, Text, View } from 'react-native';
import { Avatar } from "react-native-elements";
import { Input, Button } from 'react-native-elements';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Icon from 'react-native-vector-icons/FontAwesome';
import axios from 'axios';
 
 const CadastroContatosScreen = ({navigation}) => {

  const [getNome,setNome] = useState();
const [getCpf,setCpf] = useState();
const [getTelefone,setTelefone] = useState();
const [getEmail,setEmail] = useState();
const [getId,setId] = useState();
const [getAlterar,setAlterar] = useState();

useEffect(()=>{
  if(route.params){
      const { nome } =  route.params 
      const { telefone } =  route.params 
      const { email } =  route.params 
      const { cpf } =  route.params 
      const { id } =  route.params
      const { alterar } =  route.params
      

      setNome(nome)
      setTelefone(telefone)
      setEmail(email)
      setCpf(cpf)
      setId(id)
      setAlterar(alterar)
     
  }

},[]) 

async function inserirDados(){
        
  await axios.post('http://professornilson.com/testeservico/clientes', {
      nome: getNome,
      telefone: getTelefone,
      cpf: getCpf
    })
    .then(function (response) {
      setNome('');
      setCpf('');
      setTelefone(''); 
      showMessage({
          message: "Registro Cadastrado com sucesso",
          type: "success",
        }); 
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });     
  
}


    
    return (
      <View style={[styles.container, {
        flexDirection: "column"
      }]}>
  
        <View style={{ flex: 0.5, backgroundColor: "#00A8FF", alignItems: "center", }}>
  
          <Text h1 style={{fontSize:30, color:"white"}}>  
          <Icon onPress={()=>navigation.navigate('Lista de Contatos')} 
             name='arrow-left'
             size={30}
             color='white'
           />Contato
          </Text>

          </View>

          <View style={{ flex: 3, backgroundColor: "white" }}>
       
        <Text style={{ paddingTop: 30, textAlign: "left" }}>Nome</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
              value={getNome}
            />

            <Text style={{ paddingTop: 30, textAlign: "left" }}>Cpf</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
              value={getCpf}
            />
  
            <Text style={{ paddingTop: 30, textAlign: "left" }}>Email</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
              value={getEmail}
            />


            <Text style={{ paddingTop: 30, textAlign: "left" }}>Telefone</Text>
            <Input
              placeholder=''
              leftIcon={{ type: 'font-awesome', name: '' }}
              textAlign="center"
              value={getTelefone}
            />

            
        
        </View>
  
  
       
      <View style={{top:"80px", flex: 2 }} >

{/* <Button title="Salvar"  style={{ paddingTop: 10 }}  */}
<Button  
            title="< Voltar"
            onPress={()=>navigation.navigate('ListaContatos')}
            ></Button>


/>

{ !getAlterar ? (
    <Button style={{paddingTop:20}}
    title="Salvar"
    onPress={() => inserirDados()}
    />
    ):null}


</View>

</View>
  );
};
  

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 20,
    },
  });

  export default CadastroContatosScreen;
